@-moz-document domain("github.com") {

    /* GruvBox colours */
    :root {
        --gruv-bg0_h: #1d2021;
        --gruv-bg0_s: #32302f;

        --gruv-bg0: #282828;
        --gruv-bg1: #3c3836;
