        --gruv-bg3: #665c54;
        --gruv-bg4: #7c6f64;

        --gruv-fg0: #fbf1c7;
        --gruv-fg1: #ebdbb2;
        --gruv-fg2: #d6c4a1;
        --gruv-fg3: #bdae93;
        --gruv-fg4: #a89984;

        --gruv-gray: #928374;

        --gruv-red-light: #fb4934;
        --gruv-red-dark: #cc241d;

        --gruv-orange-light: #fe8019;
        --gruv-orange-dark: #d65d0e;

        --gruv-yellow-light: #fabd2f;
        --gruv-yellow-dark: #d79921;

