    [data-color-mode="light"][data-light-theme="dark"],
    [data-color-mode="dark"][data-dark-theme="dark"]
    {
        --color-scale-black: var(--gruv-bg0_h) !important;
        --color-scale-white: var(--gruv-fg0) !important;
        --color-scale-gray-0: var(--gruv-fg0) !important;
    }

