    }

    input[type="checkbox"] {
        border-color: var(--gruv-bg2) !important;
    }

    .blob-num {
        color: var(--gruv-bg3) !important;
    }

    .blob-num:hover {
        color: var(--color-accent-fg) !important;
    }

    /* Use the user's default monospace font. */
    code {
        font-family: monospace;
        font-size: 0.8em;
    }

    /* New shit */

    tr:has(h2) td, div[data-selector="repos-split-pane-content"] > div > div > div > div > div:has(nav), .edit-comment-hide {
